// Leere Items und Fehler rausfiltern
const items = $input.all();
const valid = items.filter(item => {
  const d = item.json;
  if (!d || Object.keys(d).length === 0) return false;
  if (d._empty) return false;
  if (d.error) return false;
  if (!d.url) return false;
  return true;
});

if (valid.length === 0) {
  return [{ json: { _empty: true, message: 'Keine Daten von Scrapern' } }];
}

return valid;

// Build a fully escaped SQL statement per item to avoid pg parameter protocol issues
function sanitizeText(value) {
  return String(value ?? '')
    // PostgreSQL rejects NUL bytes in text values and query payloads
    .replace(/\u0000/g, '')
    // Remove other control chars except tab/newline/carriage return
    .replace(/[\u0001-\u0008\u000B\u000C\u000E-\u001F\u007F]/g, ' ')
    .trim();
}

function sqlString(value) {
  if (value === null || value === undefined || value === '') return 'NULL';
  const escaped = sanitizeText(value)
    .replace(/\\/g, '\\\\')
    .replace(/'/g, "''");
  return `'${escaped}'`;
}

function sqlNumber(value) {
  if (value === null || value === undefined || value === '') return 'NULL';
  const num = Number(value);
  return Number.isFinite(num) ? String(num) : 'NULL';
}

function sqlTimestamp(value) {
  if (!value) return 'NULL';
  return `${sqlString(value)}::timestamp`;
}

function sqlTextArray(arr) {
  if (!Array.isArray(arr) || arr.length === 0) return "'{}'::text[]";
  const escaped = arr.map(v => {
    const val = sanitizeText(v)
      .replace(/\\/g, '\\\\')
      .replace(/"/g, '\\"');
    return `"${val}"`;
  });
  return `${sqlString(`{${escaped.join(',')}}`)}::text[]`;
}

return $input.all().map(item => {
  const d = item.json;

  const insertSql = `INSERT INTO articles (url, title, content, source, source_type, published_at, summary, sentiment, sentiment_score, priority, priority_reason, topics, keywords, companies, persons, regions, language, category, relevance_match, processed, sent) VALUES (${sqlString(d.url)}, ${sqlString(d.title)}, ${sqlString(d.content)}, ${sqlString(d.source)}, ${sqlString(d.source_type)}, ${sqlTimestamp(d.published_at)}, ${sqlString(d.summary)}, ${sqlString(d.sentiment)}, ${sqlNumber(d.sentiment_score)}, ${sqlString(d.priority)}, ${sqlString(d.priority_reason)}, ${sqlTextArray(d.topics)}, ${sqlTextArray(d.keywords)}, ${sqlTextArray(d.companies)}, ${sqlTextArray(d.persons)}, ${sqlTextArray(d.regions)}, ${sqlString(d.language)}, ${sqlString(d.category)}, ${sqlTextArray(d.relevance_match)}, TRUE, FALSE) ON CONFLICT (url) DO NOTHING`;

  return {
    json: {
      ...d,
      insert_sql: insertSql,
    },
  };
});

const items = $input.all();
const total = items.length;
const dupes = items.filter(i => i.json.affectedRows === 0).length;
const failed = items.filter(i => i.json.error).length;
const inserted = total - dupes - failed;

return [{
  json: {
    timestamp: new Date().toISOString(),
    total,
    inserted,
    duplicates: dupes,
    failed,
    message: `${inserted} neu, ${dupes} Duplikate, ${failed} Fehler`
  }
}];
