// RSS oder Atom Feed Items extrahieren
// Feed data comes from the current Loop batch (1 feed per iteration)
const data = $input.first().json;
const feedData = $('Loop Feeds').first()?.json || {};

let items = [];

// RSS 2.0
if (data.rss && data.rss.channel && data.rss.channel.item) {
  items = Array.isArray(data.rss.channel.item) ? data.rss.channel.item : [data.rss.channel.item];
}
// Atom
else if (data.feed && data.feed.entry) {
  items = Array.isArray(data.feed.entry) ? data.feed.entry : [data.feed.entry];
}

if (items.length === 0) {
  return [];
}

// Limit auf 20 Artikel pro Feed
items = items.slice(0, 20);

return items.map(item => {
  let title, link, pubDate, description;

  // RSS Format
  if (item.title && typeof item.title === 'string') {
    title = item.title;
    link = item.link || '';
    pubDate = item.pubDate || item.published || '';
    description = item.description || item.content || item['content:encoded'] || '';
  }
  // Atom Format
  else if (item.title && item.title._) {
    title = item.title._;
    link = item.link && item.link.href ? item.link.href : (item.id || '');
    pubDate = item.updated || item.published || '';
    description = item.summary ? (item.summary._ || item.summary) : (item.content ? (item.content._ || item.content) : '');
  }
  // Fallback for other formats
  else {
    title = typeof item.title === 'object' ? (item.title._ || item.title['#text'] || JSON.stringify(item.title)) : (item.title || 'Unbekannter Titel');
    link = typeof item.link === 'object' ? (item.link.href || item.link['@_href'] || '') : (item.link || '');
    pubDate = item.pubDate || item.published || item.updated || '';
    description = item.description || item.summary || item.content || '';
  }

  // Content extrahieren (HTML Tags entfernen)
  let content = '';
  if (typeof description === 'string') {
    content = description.replace(/<[^>]+>/g, ' ').replace(/\s+/g, ' ').trim();
  } else if (description && description._) {
    content = description._.replace(/<[^>]+>/g, ' ').replace(/\s+/g, ' ').trim();
  }

  return {
    json: {
      title: title || 'Unbekannter Titel',
      link: link,
      pubDate: pubDate,
      description: description,
      content: content,
      feedData: feedData
    }
  };
});

// Article formatieren f√ºr DB Insert
const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;
  const feedData = data.feedData || {};

  let pubDate = null;
  if (data.pubDate) {
    try {
      pubDate = new Date(data.pubDate).toISOString();
    } catch(e) {
      pubDate = null;
    }
  }

  if (data.link) {
    results.push({
      json: {
        url: data.link,
        title: data.title || 'Unbekannter Titel',
        content: data.content || '',
        source: feedData.name || 'RSS Feed',
        source_type: 'rss',
        published_at: pubDate,
        language: feedData.language || 'de',
        category: feedData.category || 'general',
        feed_id: feedData.id
      }
    });
  }
}

return results;

// Collect ALL articles from Format Article across all loop iterations
const items = $('Format Article').all();
const articles = [];

for (const item of items) {
  const articleData = item.json;
  if (articleData && articleData.url) {
    articles.push({
      json: {
        url: articleData.url,
        title: articleData.title,
        content: articleData.content,
        source: articleData.source,
        source_type: articleData.source_type,
        published_at: articleData.published_at,
        language: articleData.language,
        category: articleData.category,
        feed_id: articleData.feed_id
      }
    });
  }
}

if (articles.length === 0) {
  return [{ json: { _empty: true, message: 'No articles from RSS Feeds' } }];
}

return articles;
