// RSS oder Atom Feed Items extrahieren
const data = $input.first().json;
const feedData = $('Fetch Enabled Feeds').item($item)?.json || {};

let items = [];

// RSS 2.0
if (data.rss && data.rss.channel && data.rss.channel.item) {
  items = Array.isArray(data.rss.channel.item) ? data.rss.channel.item : [data.rss.channel.item];
}
// Atom
else if (data.feed && data.feed.entry) {
  items = Array.isArray(data.feed.entry) ? data.feed.entry : [data.feed.entry];
}

if (items.length === 0) {
  return [];
}

// Limit auf 20 Artikel pro Feed
items = items.slice(0, 20);

return items.map(item => {
  let title, link, pubDate, description;

  // RSS Format
  if (item.title && typeof item.title === 'string') {
    title = item.title;
    link = item.link || '';
    pubDate = item.pubDate || item.published || '';
    description = item.description || item.content || item['content:encoded'] || '';
  }
  // Atom Format
  else if (item.title && item.title._) {
    title = item.title._;
    link = item.link && item.link.href ? item.link.href : (item.id || '');
    pubDate = item.updated || item.published || '';
    description = item.summary ? (item.summary._ || item.summary) : (item.content ? (item.content._ || item.content) : '');
  }

  // Content extrahieren (HTML Tags entfernen)
  let content = '';
  if (typeof description === 'string') {
    content = description.replace(/<[^>]+>/g, ' ').replace(/\s+/g, ' ').trim();
  }

  return {
    json: {
      title: title || 'Unbekannter Titel',
      link: link,
      pubDate: pubDate,
      description: description,
      content: content,
      feedData: feedData
    }
  };
});

// Article formatieren f√ºr DB Insert
const item = $input.first().json;
const feedData = item.feedData || {};

let pubDate = null;
if (item.pubDate) {
  try {
    pubDate = new Date(item.pubDate).toISOString();
  } catch(e) {
    pubDate = null;
  }
}

return [{
  json: {
    url: item.link || '',
    title: item.title || 'Unbekannter Titel',
    content: item.content || '',
    source: feedData.name || 'RSS Feed',
    source_type: 'rss',
    published_at: pubDate,
    language: feedData.language || 'de',
    category: feedData.category || 'general',
    feed_id: feedData.id
  }
}];
