// Collect all recipient emails into a single item
const recipients = $input.all().map(item => item.json.email);

return {
  json: {
    recipient_emails: recipients,
    recipient_count: recipients.length
  }
};

// Generate HTML email from all articles
const articles = $input.all();
const recipientData = $('Collect Recipient Emails').first().json;
const date = new Date().toLocaleDateString('de-DE', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

const articleIds = articles.map(item => item.json.id);

// Siemens logo SVG as base64 data URI
const logoBase64 = 'data:image/svg+xml;base64,';

let html = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <style>
    body { font-family: Arial, Helvetica, sans-serif; line-height: 1.6; color: #66667e; max-width: 800px; margin: 0 auto; padding: 20px; background-color: #f3f3f0; }
    .header { margin-bottom: 30px; padding-top: 30px; padding-bottom: 30px; border-bottom: 2px solid #009999; }
    .logo { max-width: 200px; height: auto; margin-top: 20px; margin-bottom: 25px; }
    h1 { color: #000028; font-weight: 700; margin: 0; padding-top: 10px; font-size: 1.5em; }
    .article { margin-bottom: 30px; padding: 20px; background: #ebebee; border-left: 4px solid #009999; }
    .article h2 { margin-top: 0; color: #000028; font-weight: 700; }
    .article-meta { color: #9999a9; font-size: 0.9em; margin-bottom: 10px; }
    .summary { margin: 15px 0; }
    .summary-short { font-weight: 700; color: #000028; margin-bottom: 10px; }
    .summary-long { color: #66667e; }
    .read-more { display: inline-block; margin-top: 10px; color: #009999; text-decoration: none; font-weight: 600; }
    .read-more:hover { color: #00c1b6; }
    .footer { margin-top: 40px; padding-top: 20px; border-top: 1px solid #ccccd4; color: #9999a9; font-size: 0.85em; text-align: center; }
  </style>
</head>
<body>
  <div class="header">
    <img src="${logoBase64}" alt="Siemens" class="logo">
    <h1>Dein News Digest - ${date}</h1>
  </div>
  <p>Hier sind deine ${articles.length} wichtigsten Nachrichten von heute:</p>
`;

for (const item of articles) {
  const article = item.json;
  const publishedDate = article.published_at ? 
    new Date(article.published_at).toLocaleDateString('de-DE') : 
    'Datum unbekannt';

  html += `
  <div class="article">
    <h2>${article.title}</h2>
    <div class="article-meta">
      ${publishedDate} | ${article.source}
    </div>
    <div class="summary">
      <div class="summary-short">${article.summary_short || 'Keine Kurzzusammenfassung verfügbar'}</div>
      <div class="summary-long">${article.summary_long || 'Keine ausführliche Zusammenfassung verfügbar'}</div>
    </div>
    <a href="${article.url}" class="read-more" target="_blank">Weiterlesen</a>
  </div>
  `;
}

html += `
  <div class="footer">
    <p>Dieser Digest wurde automatisch von deinem News AI Agent generiert.</p>
  </div>
</body>
</html>
`;

return {
  json: {
    html_content: html,
    article_count: articles.length,
    article_ids: articleIds,
    recipient_emails: recipientData.recipient_emails,
    recipient_count: recipientData.recipient_count,
    subject: `News Digest - ${date} (${articles.length} Artikel)`
  }
};

// Split recipients into individual items for loop
const emailData = $input.first().json;
const emails = emailData.recipient_emails || [];

// Return one item per recipient
return emails.map(email => ({
  json: {
    to_email: email,
    html_content: emailData.html_content,
    subject: emailData.subject,
    article_ids: emailData.article_ids,
    article_count: emailData.article_count
  }
}));

// Final success message
const emailData = $('Generate HTML Email').first().json;

return {
  json: {
    timestamp: new Date().toISOString(),
    articles_sent: emailData.article_count,
    recipients_count: emailData.recipient_count,
    status: 'success',
    message: `Successfully sent digest with ${emailData.article_count} articles to ${emailData.recipient_count} recipients`
  }
};
